$(document).ready(function(){var a=0;switch($(window).scroll(function(){a=$(window).scrollTop(),a>=100?$(".primary-header").addClass("scrolled-nav"):a<100&&$(".primary-header").removeClass("scrolled-nav")}),window.location.href){case"/index":$("#work-nav").addClass("selected");case"/about":$("#info-nav").addClass("selected");case"/contact":$("#contact-nav").addClass("selected")}});